
public class bit_oprator_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int a=10,b=20;
	    int c=a&b;
	    System.out.print(c);
	    int d=a|b;
	    System.out.print(d);
	    int e=a^b;
	    System.out.print(e);
	    int f=c+d+e;
	    System.out.print(~f);
	}

}
