import java.util.Scanner;

public class nnnnnn {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner input = new Scanner(System.in);
		int n = input.nextInt();
		for (int i = 1; i <= n; i++) {
			
 for (int j = n; j >= n-(n-i); j--) {
	 if (j == i) {
		 System.out.print("*");
		 continue;
	}
	System.out.print(j);
	
	
}
 
 
 for (int j2 = i; j2 > 1; j2--) {
	
	 
		System.out.print(j2);
	}
 System.out.println(" ");

		}

	}

}
