
public class SwepTwoNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// now we are the defining the two variable 
		int x =20 ; 
		int y = 40 ; 
		
		// now sweeping 
		int temp ; 
		
		temp = x ; 
		x = y ; 
		y = temp ; 
		
		System.out.println(x+" "+y);
	}

}
