
public class assmentoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  // now we learn the assignment operators
		int x = 5;
		//write the assignment oprerators 
		x += 5;//10 5+5 =10
		System.out.println(x);
		x -= 5 ; //10-5 =5 
		System.out.println(x);
		x *= 5 ; //5*5 = 25 
		System.out.println(x);
		x /= 5 ; //25/5 = 5
		System.out.println(x);
		// now we are the again use of modulus operators
		x %= 5 ; // 5/5 = 0 remainder
		System.out.println(x);
		// now we see that the changed of the value of the x according to operation 
	}

}
