import java.util.*;
public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int n = 24 ;
  int q = 2 ;
  int j = 0 ;
  int k = 1 ;
  int i ;
  for (i = n; i >= q; --i) {
	 
	   j += i;
	   k *= i ;
	 
	  
}
  System.out.println(j);
  System.out.println(k);
	}

}
