import java.util.Iterator;

public class numberpattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// so i try to learn the number pattern that are implemented by the nested for loop 
		// so i using hear the for loop of implemented the number pattern 
		for (int i = 1; i < 10; i++) {
			for (int j = 1 ; j <=i; j++) {
				System.out.print(j);
				
			}
			System.out.println(" ");
			
		}
	}

}
